import React, {useState} from 'react';
import './App.css';
import ToDoLIst from './ToDoLIst';
import {TaskType} from './ToDoLIst'

// import ToDoLIst, {TaskType} from "./ToDoLIst";
//
// function App() {
//     const tasks: Array<TaskType> = [
//         {id: 1, title: 'HTML', isDone: true},
//         {id: 2, title: 'CSS', isDone: true},
//         {id: 3, title: 'TJS/S', isDone: false},
//     ]
//     return (<div>
//         <ToDoLIst title={'test1'} tasks={tasks}
//         />
//
//
//     </div>)
// }

// type TaskType = {
//     id: number,
//     isDone: boolean,
//     title:string,
// }

function App() {

    const [state, setState] = useState( [
        {id: 1, title: 'HTML&CSS', isDone: true,},
        {id: 2, title: 'JS', isDone: true,},
        {id: 3, title: 'ReactJS', isDone: false,},
        {id: 4, title: 'Redux', isDone: false,}
    ]);
    let mem: any;
    // let arr = useState(tasks);
    // let state = arr[0];
    // let setState = arr[1];

    function removeTask(id: number) {
        let filtredTasks = state.filter(item => {
            return item.id !== id
        })
        setState(filtredTasks);
    }


    function showActiveTasks(){
        mem = state;
        let filtredActiveTasks = state.filter(item => item.isDone === false);
        setState(filtredActiveTasks)
    }

    function showCompletedTasks(){
        mem = state;
        let filtredCompletedTasks = state.filter(item => item.isDone === true);
        setState(filtredCompletedTasks)
    }

    function showAllTasks(){
        setState(mem);
    }


    return (
        <div className='App'>
            <ToDoLIst
                title={'Learn'}
                tasks={state}
                removeTask={removeTask}
                showAllTasks={showAllTasks}
                showActiveTasks = {showActiveTasks}
                showCompletedTasks = {showCompletedTasks}
            />
        </div>

    );
}

export default App;
