type ToDoListType = {
    title: string,
    tasks: Array<TaskType>,
    removeTask: Function,
    showAllTasks: Function,
    showActiveTasks: Function,
    // showCompletedTasks: Function,
}

export type TaskType = {
    id: number,
    isDone: boolean,
    title: string,
}

const ToDoLIst: FC<ToDoListType> = (props) => {
    return (
        <>
            <div className="App">
                <div>
                    <h3>{props.title}</h3>
                    <div>
                        <input/>
                        <button>+</button>
                    </div>
                    <ul>
                        {props.tasks.map(item => {
                            return <li>
                                <input type="checkbox" checked={item.isDone}/>
                                <span>{item.title}</span>
                                <button onClick={() => {
                                    props.removeTask(item.id)
                                }}>X
                                </button>
                            </li>
                        })}
                    </ul>
                    <div>
                        <button onClick = {() => props.showAllTasks()}>All</button>
                        <button onClick = {() => props.showActiveTasks()}>Active</button>
                        <button>Completed</button>
                    </div>
                </div>
            </div>
        </>
    )
}

export default ToDoLIst;
