function App() {

    const [tasks, setTasks] = useState( [
        {id: 1, title: 'HTML&CSS', isDone: true,},
        {id: 2, title: 'JS', isDone: true,},
        {id: 3, title: 'ReactJS', isDone: false,},
        {id: 4, title: 'Redux', isDone: false,}
    ]);
    const [filtered, filter] = useState('All')
    // let arr = useState(tasks);
    // let state = arr[0];
    // let setState = arr[1];

    function removeTask(id: number) {
        let filtredTasks = tasks.filter(item => {
            return item.id !== id
        })
        setTasks(filtredTasks);
    }

    let tasksForToDoList = tasks;

    if(filtered == 'Completed'){
        tasksForToDoList = tasks.filter(item => item.isDone === true)
    }
    if(filtered == 'Active'){
        tasksForToDoList = tasks.filter(item => item.isDone === false)
    }

    // function showActiveTasks(){
    //     mem = state;
    //     let filtredActiveTasks = state.filter(item => item.isDone === false);
    //     setState(filtredActiveTasks)
    // }

    // function showCompletedTasks(){
