    //     setState(filtredCompletedTasks)
    // }

    // function showAllTasks(){
    //     setState(mem);
    // }


    return (
        <div className='App'>
            <ToDoLIst
                title={'Learn'}
                tasks={tasks}
                removeTask={removeTask}
                // showAllTasks={showAllTasks}
                // showActiveTasks = {showActiveTasks}
                // showCompletedTasks = {showCompletedTasks}
            />
        </div>

    );
}

export default App;
